import { configure } from '@testing-library/react';
import { getActiveElement } from '@testing-library/user-event/dist/utils';
import React, { useState } from 'react'


export default function Form(){
    const[ text, nxttext]=useState("Enter the text") ;
    const handleclick =()=>{
        console.log("Clicked the button");
        let newtxt=text.toUpperCase();
        document.getElementById("exampleFormControlTextarea1").value=newtxt;
    }
    const handlechange =(event) =>{
        console.log("on change")
        nxttext(event.target.value);

    }
return(
    <>
    <div className="container mx-3">
    <div className="mb-3">
  <label for="exampleFormControlTextarea1" className="form-label">Enter Text</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" placeholder={text} onChange={handlechange}></textarea>
  <button className="btn btn-primary btn-lg" type="button" id="button-addon11" onClick={handleclick}>To Uppercase</button>
    </div>
    </div>
    </>

);
}
