import React, { useState } from 'react'


export default function Form(){
    const[ text, nxttext]=useState("Enter the text") ;
    const[ newtxt, newnewtxt]=useState() ;
    const handleclickUC =()=>{
        console.log("Clicked the UC button");
        newnewtxt(text.toUpperCase());
        document.getElementById("exampleFormControlTextarea1").value=newtxt;
    }
    const handlechange =(event) =>{
        console.log("on change")
        nxttext(event.target.value);

    }
    const handleclickLC =()=>{
        console.log("Clicked the LC button");
        newnewtxt(text.toLowerCase());
        document.getElementById("exampleFormControlTextarea1").value=newtxt;
    }
    const handleclickLine =()=>{
        console.log("Clicked the line wise button");
        let line= text.split('.');
        newnewtxt(line.forEach(element => {
            <li>
                <ul>
                    {element}
                </ul>
            </li>
        })) 
        document.getElementById("exampleFormControlTextarea1").value=newtxt;
    }
return(
    <>
    <div className="container my-3">
    <div className="mb-3">
  <label for="exampleFormControlTextarea1" className="form-label">Enter Text below</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" placeholder={text} onChange={handlechange}></textarea>
  <button className="btn btn-primary m-2" type="button" onClick={handleclickUC}>To Uppercase</button>
  <button className="btn btn-primary m-2" type="button" onClick={handleclickLC}>To Lowercase</button>
  <button className="btn btn-primary m-2" type="button" onClick={handleclickLine}>Point Wise</button>
  </div>
  <div>
    <h3>Summary</h3>
    <h5>Input Text</h5>
    <p>{text}</p>
    <h5>Changed Text</h5>
    <p>{newtxt}</p>
  </div>
    </div>

    </>

);
}
